import React, { useState } from "react";

export default function MacroInputs() {
  const [carbs, setCarbs] = useState(0);
  const [protein, setProtein] = useState(0);
  const [fat, setFat] = useState(0);
  const [calculatedCalories, setCalculatedCalories] = useState(0);

  const handleCarbsChange = (e) => setCarbs(parseInt(e.target.value, 10));
  const handleProteinChange = (e) => setProtein(parseInt(e.target.value, 10));
  const handleFatChange = (e) => setFat(parseInt(e.target.value, 10));

  const calculateCalories = () => {
    const totalCalories = carbs * 4 + protein * 4 + fat * 9;
    setCalculatedCalories(totalCalories);
  };

  return (
    <div>
      <section>
        <form action="" className="macrosForm">
          <div className="calculated-macros-display">
            <h1 id="calculated-calories-display">{calculatedCalories} calories</h1>
          </div>

          <div className="macrosForm__inputGroup">
            <h1>Grams of Carbs</h1>
            <input
              type="number"
              id="carbs-input"
              value={carbs}
              onChange={handleCarbsChange}
            />
          </div>

          <div className="macrosForm__inputGroup">
            <h1>Grams of Protein</h1>
            <input
              type="number"
              id="protein-input"
              value={protein}
              onChange={handleProteinChange}
            />
          </div>

          <div className="macrosForm__inputGroup">
            <h1>Grams of Fat</h1>
            <input
              type="number"
              id="fat-input"
              value={fat}
              onChange={handleFatChange}
            />
          </div>

          <div className="macrosForm__inputGroup mt-5">
            <button onClick={calculateCalories} className="btn btn-light btn-lg" type="button">
              Calculate
            </button>
          </div>
        </form>
      </section>
    </div>
  );
}
