import logo from "./logo.svg";

//import './index.css';
import Header from "./components/header";
import MacroInputs from "./components/macroInputs";

function App() {
  return (
    <div className="App">
      <div className="app-header">
        <Header />
      </div>

      <div className="app-body">
        <div className="my-4 text-jusfity">
          <h1>How are calories calculated?</h1>
          <p className="calories-explanation-panel">
            Calories are calculated from 3 main macronutrients.
            <br /> <span className="accent">Carbs, Protein and Fat.</span>
            <br />
            <br />
            1g of carbs is equivaltent to 4 calories <br />
            1g of protein is equivaltent to 4 calories
            <br />
            1g of fat is equivaltent to 9 calories
            <br />
          </p>
        </div>
      </div>

      <div className="app-form">
        <MacroInputs />
      </div>
    </div>
  );
}

export default App;
