import React from "react";

export default function macroInputs() {
  return (
    <div>
      <section>
        <form action="" className="macrosForm">
          <div className="calculated-macros-display">
            <h1 id="calculated-calories-display"></h1>
          </div>

          <div className="macrosForm__inputGroup">
            <h1>Grams of Carbs</h1>
            <input type="text" id="carbs-input"/>
          </div>

          <div className="macrosForm__inputGroup">
            <h1>Grams of Protein</h1>
            <input type="text" id="protein-input"/>
          </div>

          <div className="macrosForm__inputGroup">
            <h1>Grams of Fat</h1>
            <input type="text" id="fat-input"/>
          </div>

          <div className="macrosForm__inputGroup mt-5">
            <button onClick={calculateCalories()} className="btn btn-light btn-lg " type="button">Calculate</button>
          </div>


        </form>
      </section>
    </div>
  );
}

//4 calories per gram of carbs
const carbs = document.getElementById('carbs-input');
//4 calories per gram of carbs
const protein = document.getElementById('protein-input');
//9 calories per gram of carbs
const fat = document.getElementById('fat-input')

//calculation of calories
const calculatedCalories = (carbs+protein+fat);


//displaying the result in an element
const display = document.getElementById('calculated-calories-display');

function calculateCalories() { 

  display.innerHTML = calculatedCalories;

}

calculateCalories();
